onClick:
{{
  (() => {
    const row = usersTable.selectedRow;

    if (!row) {
      showAlert("Please select a user first.", "warning");
      return;
    }

    // 1️⃣ Already registered
    if (row.IS_REGISTERED === "true") {
      showAlert(
        "This user is already registered. Please use the (Manage) button or the (Application Users) tab to manage the user and their dependents.",
        "warning"
      );
      return;
    }

    // 2️⃣ Not Head of Family
    if (row.HOF_ID !== null) {
      showAlert(
        "To activate this user, the user must be the Head of Family and currently inactive.",
        "error"
      );
      return;
    }

    // 3️⃣ Eligible → open modal
    showModal(Modal1.name);
  })()
}}
