DECLARE
  v_subs_id NUMBER := {{ this.params.subsId }};
  v_hof_id   NUMBER;
BEGIN
  -- Get the selected user's HOF_ID
  SELECT HOF_ID INTO v_hof_id
  FROM APP_USERS
  WHERE SUBS_ID = v_subs_id;

  IF v_hof_id IS NULL THEN
    -- User is head of family: delete user + dependents
    DELETE FROM DEPENDENTS_PHONE WHERE HOF_ID = v_subs_id;
    DELETE FROM APP_USERS WHERE HOF_ID = v_subs_id;
    DELETE FROM APP_USERS WHERE SUBS_ID = v_subs_id;
  ELSE
    -- User is dependent: delete only their record
    DELETE FROM APP_USERS WHERE SUBS_ID = v_subs_id;
  END IF;
END;
