body {
  font-family: "Inter", sans-serif;
  margin: 0;
  padding: 0;
  background-color: transparent;
  color: #1e1e1e;
}

.patient-card {
  background: #ffffff;
  border-radius: 20px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
  padding: 28px 32px;
  width: 100%;
  box-sizing: border-box;
}

.patient-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 20px;
}

.header-left {
  display: flex;
  align-items: center;
  gap: 16px;
}

.patient-avatar {
  width: 72px;
  height: 72px;
  background: linear-gradient(135deg, #6a5acd, #8a7ffb);
  border-radius: 50%;
  color: #fff;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  letter-spacing: 1px;
  flex-shrink: 0;
}

.patient-info {
  display: flex;
  flex-direction: column;
}

.patient-name {
  font-size: 20px;
  font-weight: 300;
  color: #1a1a1a;
  margin-bottom: 4px;
}

.patient-id {
  font-size: 14px;
  color: #6e6e6e;
}

.status-badge {
  font-size: 13px;
  padding: 6px 14px;
  border-radius: 9999px;
  font-weight: 300;
  text-transform: capitalize;
}

.status-active {
  background-color: #e9f6ec;
  color: #187b39;
}

.divider {
  border-top: 1px solid #f0f0f0;
  margin: 16px 0 20px;
}

.info-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 16px 24px;
}

.info-item {
  display: flex;
  flex-direction: column;
}

.info-label {
  font-size: 12px;
  color: #888;
  letter-spacing: 0.4px;
  margin-bottom: 4px;
  text-transform: uppercase;
}

.info-value {
  font-size: 15px;
  font-weight: 200;
  color: #1a1a1a;
}

