function updateCard(model) {
  const subs = model?.subsFullInfo?.subs || {};
  const insurance = model?.subsFullInfo?.insuranceCompanyNameEn || "-";
  const doctor = model?.doctorName || "-";
  const url = model?.url || {};
  const name = subs.nameEnglish || "-";
  const initials = name ? name.split(" ").filter(Boolean).map(n => n[0]).join("").slice(0, 2).toUpperCase() : "--";
  const nationalNo = subs.nationalNO || "-";
  const mobile = subs.mobilePhone || "-";
  const visitNo = url.vstNo || "-";
  const visitDate = url.vstDate || model?.today || "-";
  const speciality = url.docSpec || "-";
  document.getElementById("avatar").textContent = initials;
  document.getElementById("patient-name").textContent = name;
  document.getElementById("patient-id").textContent = `National ID: ${nationalNo} • Contact: ${mobile}`;
  document.getElementById("visit-number").textContent = visitNo;
  document.getElementById("visit-date").textContent = visitDate;
  document.getElementById("risk-carrier").textContent = insurance;
  document.getElementById("doctor").textContent = doctor;
  document.getElementById("speciality").textContent = speciality;
}
if (window.appsmith) {
  updateCard(appsmith.model);
  appsmith.onModelChange(updateCard);
}

